import { useState } from "react";

const messages = [
  "Learn React ⚛️",
  "Apply for jobs 💼",
  "Invest your new income 🤑",
];

let App = () => {
  // Desctructring array
  const [step, setStep] = useState(1);
  const [open, setOpen] = useState('open');

  function closeSteps(){
    if (open == 'open'){
      setOpen('open');
    } else {
      setOpen('close');
    }
  }

  function handlePrevious() {
    if (step > 1) {
      setStep(step - 1);
      return;
    }
  }
  function handleNext() {
    if (step <= 2) {
      setStep(step + 1);
      return;
    }
  }
  return (
    <div className="steps">
      <div className="numbers">
        <div className={step === 1 ? "active" : ""}>1</div>
        <div className={step === 2 ? "active" : ""}>2</div>
        <div className={step === 3 ? "active" : ""}>3</div>
      </div>
      <div onClick={closeSteps} className="close">x</div>
      <div>{open}</div>
      <p className="message">
        Step {step}: {messages[step - 1]}
      </p>
      <div className="buttons">
        <button
          onClick={handlePrevious}
          style={{ backgroundColor: "#7950f2", color: "#fff" }}
        >
          Previous
        </button>
        <button
          onClick={handleNext}
          style={{ backgroundColor: "#7950f2", color: "#fff" }}
        >
          Next
        </button>
      </div>
    </div>
  );
};
export default App;
